document.addEventListener("DOMContentLoaded",function(){getToggles=function(){document.querySelectorAll("[data-idea-toggle]").forEach(function(el){var targetVal=el.getAttribute("data-idea-toggle");if(0==targetVal.length)var target=el.querySelector("[data-idea-target]");else target=document.querySelector("[data-idea-target='"+targetVal+"']");el.addEventListener("click",function(){target.classList.toggle("-is-visible")})})},getToggles()},!1);